const generateForm = document.getElementById('generate');
const loremTexts = document.getElementById('loremtext');
const resultText = document.getElementById('results');

// Get Number of Submit Value from Users to Generate
// If Input Value is > 100 will Display only One Result
// If Input Value is <= 100 will Display Based on Input Value
generateForm.addEventListener('submit', (e) => {
   e.preventDefault();

   const numberValue = parseInt(loremTexts.value);
   const randomValue = Math.floor(Math.random() * dummyTexts.length);

   if (isNaN(numberValue) || numberValue <= 0 || numberValue > 100) {
      resultText.innerHTML = `<p class="paragraph">${dummyTexts[randomValue]}</p>`;
   } else {
      let textParagraph = dummyTexts.slice(0, numberValue);
      textParagraph = textParagraph
         .map(function (texts) {
            return `<p class="paragraph">${texts}</p>`;
         })
         .join('');

      resultText.innerHTML = textParagraph;
   }
});

// When the User Clicked the Copy Button
// Then Copy the Result to The Clipboard
(function () {
   const copiedTexts = document.querySelector('.copied');

   copiedTexts.addEventListener('click', () => {
      let range = document.createRange();
      range.selectNodeContents(document.getElementById('results'));

      let selection = window.getSelection();
      selection.removeAllRanges();
      selection.addRange(range);

      document.execCommand('copy');
   });
})();
